mapper.select:
select * from user_view 
where c_idx = #{ c_idx } 
order by idx asc

select * from user_view 


	
		id = #{ id }
	
	
		and password = #{ password }

select * from ( select rank() over(order by idx ) rank, p.* from (select * from user_view
idx like '%' || #{ idx } || '%'
or g_position like '%' || #{ g_position } || '%'
or name like '%' || #{ name } || '%'
or id like '%' || #{ id } || '%'
)p ) where rank between #{ start } and #{ end }

mapper.delete:
		delete from user_manager
		where idx in
		
     	#{item}
